package com.github.shmvanhouten.adventofcode.day2

import org.hamcrest.MatcherAssert.assertThat
import org.hamcrest.Matchers.`is`
import org.junit.Test

class BathroomCodeCalculatorTest {

    @Test
    fun itShouldCalculate6FromR() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("R"))
        assertThat(code, `is`("6"))
    }

    @Test
    fun itShouldCalculate4FromL() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("L"))
        assertThat(code, `is`("4"))
    }

    @Test
    fun itShouldCalculate2FromU() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("U"))
        assertThat(code, `is`("2"))
    }


    @Test
    fun itShouldCalculate8FromD() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("D"))
        assertThat(code, `is`("8"))
    }

    @Test
    fun itShouldCalculate3FromRU() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("RU"))
        assertThat(code, `is`("3"))
    }

    @Test
    fun itShouldCalculate6FromRRR() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("RRR"))
        assertThat(code, `is`("6"))
    }

    @Test
    fun itShouldCalculate1and8FromLUandDDR() {
        val finger = FingerOrigImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("LU", "DDR"))
        assertThat(code, `is`("18"))
    }


    @Test
    fun itShouldDoTheActualChallenge() {
        val finger = FingerOrigImpl()
        //todo: make the input from the website fit the input into the calculator
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("",
                "",
                "",
                "",
                ""))
        assertThat(code, `is`("84452"))
    }

    @Test
    fun `With a new keypad outlay, it should calculate 'B' from 'R R D D`() {
        val finger = FingerNewImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("RRDD"))
        assertThat(code, `is`("D"))
    }

    @Test
    fun `With a new keypad outlay, it should find a different code for the challenge`() {
        val finger = FingerNewImpl()
        val calculator = BathroomCodeCalculator(finger)
        val code: String = calculator.calculateCodeFromInstructions(listOf("",
                "",
                "",
                "",
                ""))
        assertThat(code, `is`("D65C3"))
    }

}